import type { Assertion, AsymmetricMatchersContaining } from 'vitest'

export interface CustomMatchers<R = unknown> {
  toMatchSequence(...matchers: Array<(arg: any) => boolean>): R
}

declare module 'vitest' {
  export interface Assertion<T = any> extends CustomMatchers<T> {}
  export interface AsymmetricMatchersContaining extends CustomMatchers {}
}

declare global {
  namespace jest {
    export interface Matchers<R> extends CustomMatchers<R> {}
  }
}
