// Polyfill for ES2022's .at() method on string/array prototypes, added to TypeScript in 4.6.
// TODO: these methods are not used within @types/node, and should be removed at the next
// major @types/node version; users should include the es2022 TypeScript libraries
// if they need these features.

export interface RelativeIndexable<T> {
    at(index: number): T | undefined;
}

export interface String extends RelativeIndexable<string> {}
export interface Array<T> extends RelativeIndexable<T> {}
export interface ReadonlyArray<T> extends RelativeIndexable<T> {}
export interface Int8Array extends RelativeIndexable<number> {}
export interface Uint8Array extends RelativeIndexable<number> {}
export interface Uint8ClampedArray extends RelativeIndexable<number> {}
export interface Int16Array extends RelativeIndexable<number> {}
export interface Uint16Array extends RelativeIndexable<number> {}
export interface Int32Array extends RelativeIndexable<number> {}
export interface Uint32Array extends RelativeIndexable<number> {}
export interface Float32Array extends RelativeIndexable<number> {}
export interface Float64Array extends RelativeIndexable<number> {}
export interface BigInt64Array extends RelativeIndexable<bigint> {}
export interface BigUint64Array extends RelativeIndexable<bigint> {}
